import React, { useState } from 'react'
import '../css/Container.css'
const container = () => {

    const data = [
        {
            "name": "Cosmetics",
            "productList": [
                {
                    "name": "Hair Oil",
                    "price": 122
                },
                {
                    "name": "Face wash",
                    "price": 123
                }
            ]
        },
        {
            "name": "Household",
            "productList": [
                {
                    "name": "Bed",
                    "price": 9854

                },
                {
                    "name": "Sofa",
                    "price": 9842
                }
            ]
        },
        {
            "name": "Electronics",
            "productList": [
                {
                    "name": "Phone",
                    "price": 9876
                },
                {
                    "name": "TWS",
                    "price": 789
                }
            ]
        }
    ]
    const [Cart,setCart] = useState([" "]);
    function Addtocart (nameindex,productindex){
        const Pname = data[nameindex].productList[productindex].name
        console.log(Pname);
        const Pprice =  data[nameindex].productList[productindex].price
        console.log(Pprice);
        let arr = {"name":Pname, "price":Pprice}
        setCart(...Cart,arr)
    }

    return (
        <div className='main'>
            {/* <h1>E-Commerce</h1> */}
            <div className='category'>
                {data.map((a, nameindex) => {
                    return (
                        <>
                            <div key={a}>
                            </div>
                            <h2>{a.name}</h2>


                            <div className='product'>
                                {a.productList.map((b, productindex) => {
                                    return (
                                        <>
                                        <div className='item'>
                                            <div key={b}>
                                            </div>
                                            <h3>Item : {b.name}</h3>
                                            <h3>Price : â‚¹ {b.price}</h3>

                                            <button onClick={()=>Addtocart(nameindex,productindex)} >Add To Cart</button>
                                            <button>Remove To Cart</button>
                                            </div>
                                        </>
                                    )
                                })}
                            </div>

                        </>
                    )
                })}
                {/* a = full array
                    b = all productList */}
            </div>
        </div>
    )
}

export default container
